import React, { useEffectm} from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import Footer from '../components/Footer';
import {
  reset,
  getAllProduct,
  deleteProduct,
} from '../features/product/productSlice';

const ProductListPage = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  // const [productList, setProductList] = useState([]);

  const { products, isError, message } = useSelector(
    (state) => state.product
  );
  const massDelete = async (e) => {
    var x = document.getElementsByClassName('delete-checkbox');
    const arr = [];
    for (let i = 0; i < x.length; i++) {
      if (x[i].checked) {
        console.log(x[i].id);
        arr.push(x[i].id);
      }
    }
    dispatch(deleteProduct(arr));
  };
  useEffect(() => {
    if (isError) {
      console.log(message);
    }
    dispatch(getAllProduct());
    return () => {
      dispatch(reset());
    };
  }, [navigate, isError, message, dispatch]);

  return (
    <>
      <div className="container">
        <header>
          <div className="heading">Product List</div>
          <div style={{ padding: '15px' }}>
            <button onClick={() => navigate('/add-product')}>ADD</button>
            <button id="delete-product-btn" onClick={massDelete}>
              MASS DELETE
            </button>
          </div>
        </header>
        <hr />
        {products.length > 0 ? (
          <div className="grid">
            {products.map((product, index) => (
              <div className="box" key={index}>
                <label htmlFor={product.id}>
                  <input
                    className="delete-checkbox"
                    type="checkbox"
                    id={product.id}
                    name={product.name}
                    value={product.id}
                  />
                  <br />
                  <div className="box-text">
                    <p>{product.sku}</p>
                    <p>{product.name}</p>
                    <p>{product.price}$</p>
                    {product.type === 'Book' && product.weight && (
                      <p>Size: {product.weight} KG</p>
                    )}
                    {product.type === 'DVD' && product.size && (
                      <p>Weight: {product.size} MB</p>
                    )}
                    {product.type === 'Furniture' &&
                      product.height &&
                      product.width &&
                      product.length && (
                        <p>
                          Dimension: {product.height} x {product.width} x{' '}
                          {product.length}
                        </p>
                      )}
                  </div>
                </label>
              </div>
            ))}
          </div>
        ) : (
          <div>No Product Availabe kindly Add Products</div>
        )}
      </div>
      <Footer />
    </>
  );
};

export default ProductListPage;
