import axios from 'axios';

const API_URL = 'http://localhost:8080/product';

// Get all Products
const getAllProduct = async () => {
  const response = await axios.get(API_URL);
  return response.data;
};

// Create a product
const createAccountInfo = async (accountInfoData, token) => {
  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };
  // for the body we need to send the text as I have done below
  const response = await axios.post(API_URL, { text: accountInfoData }, config);
  // Change this is to response.data, to collect the array of objects being sent from the backend
  return response.data;
};

const productService = {
  getAllProduct
}

export default productService;
