import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import Spinner from '../components/Spinner';
import axios from 'axios';
import { reset, createProduct } from '../features/product/productSlice';
const AddProductPage = () => {
  const [productData, setProductData] = useState({
    name: 'Acme Disc',
    type: 'Book',
    SKU: 'JVC200528',
    price: 20.0,
    weight: 400,
  });
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const onSubmit = (e) => {
    // e.preventDefault();
    // dispatch(createProduct(productData));
    // productData([]);
    axios
      .post('http://localhost:8080/product', productData)
      .then(function (response) {
        console.log(response);
      });
  };

  return (
    <div onClick={() => onSubmit()}>
      <>
        <div className="container">
          <header>
            <div className="heading">Product Add</div>
            <div style={{ padding: '15px' }}>
              <button>Save</button>
              <button onClick={() => navigate('/')}>Cancel</button>
            </div>
          </header>
          <hr />
          <section className="form">
            <form id="product_form" onSubmit={onSubmit}>
              <div className="form-group">
                {/* <label>SKU</label> */}
                <input
                  type="text"
                  className="form-control"
                  id="sku"
                  name="SKU"
                  placeholder="Enter SKU"
                />
              </div>
              <div className="form-group">
                <input
                  type="text"
                  className="form-control"
                  id="name"
                  name="name"
                  placeholder="Enter product name"
                />
              </div>
              <div className="form-group">
                <input
                  type="float"
                  className="form-control"
                  id="password"
                  name="password"
                  placeholder="Enter your Password"
                />
              </div>
              <div className="form-group">
                <input
                  type="password"
                  className="form-control"
                  id="password2"
                  name="password2"
                  placeholder="Confirm your Password"
                />
              </div>
              <div className="form-group">
              <label for="productType">Type Switcher:</label>
                <select id="productType" name="productType">
                  <option value="volvo">DVD</option>
                  <option value="saab">Furniture</option>
                  <option value="fiat">Fiat</option>
                </select>
              </div>
            </form>
          </section>
        </div>
      </>
    </div>
  );
};

export default AddProductPage;
