import axios from 'axios';

const API_URL = 'https://ayoola123.000webhostapp.com/product';
// const API_URL = 'http://localhost:8080/product';
const API_URL1 = 'https://ayoola123.000webhostapp.com/products/delete';
// const API_URL1 = 'http://localhost:8080/products/delete';


// Get all Products
const getAllProduct = async () => {
  const response = await axios.get(API_URL);
  return response.data;
};

// Create a product
const createProduct = async (productInfo) => {
  // const response = await axios.post(API_URL, productInfo);
  // return response.data;
  const response = await fetch(API_URL, {
    method: 'POST', // *GET, POST, PUT, DELETE, etc.
    // mode: 'cors', // no-cors, *cors, same-origin
    // cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
    // credentials: 'same-origin', // include, *same-origin, omit
    // headers: {
    //   'Content-Type': 'application/json'
    //   // 'Content-Type': 'application/x-www-form-urlencoded',
    // },
    // redirect: 'follow', // manual, *follow, error
    // referrerPolicy: 'no-referrer', // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
    body: JSON.stringify(productInfo) // body data type must match "Content-Type" header
  });
  return response.data; // parses JSON response into native JavaScript objects

};

const deleteProduct = async (productList) => {
  // const response = await axios.delete(API_URL, { data: { productList } });
  const response = await fetch(API_URL1, {
    method: 'POST', // *GET, POST, PUT, DELETE, etc.
    // mode: 'cors', // no-cors, *cors, same-origin
    // cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
    // credentials: 'same-origin', // include, *same-origin, omit
    // headers: {
    //   'Content-Type': 'application/json'
    //   // 'Content-Type': 'application/x-www-form-urlencoded',
    // },
    // redirect: 'follow', // manual, *follow, error
    // referrerPolicy: 'no-referrer', // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
    body: JSON.stringify({ productList }) // body data type must match "Content-Type" header
  });
  return response.data; 
};

const productService = {
  getAllProduct,
  createProduct,
  deleteProduct,
};

export default productService;
