import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import {
  reset,
  createProduct,
} from '../features/product/productSlice';
import Footer from '../components/Footer';
const AddProductPage = () => {
  const [productData, setProductData] = useState({
    name: '',
    type: '',
    SKU: '',
    price: 0,
    size: null,
    weight: null,
    height: null,
    width: null,
    length: null,
  });

  const { name, type, SKU, price, size, weight, height, width, length } =
    productData;
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const onChange = (e) => {
    setProductData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const onSubmit = (e) => {
    e.preventDefault();
    if (!name) {
      // toast.error('Passwords do not match');
      return;
    } else {
      const data = {
        name,
        type,
        SKU,
        price,
        size,
        weight,
        height,
        width,
        length,
      };
      dispatch(createProduct(data));
    }
  };
  // const onSubmit = (e) => {
  //   e.preventDefault();
  //   dispatch(createProduct(productData));
  //   // productData([]);
  // };

  return (
    <>
      <div className="container">
        <header>
          <div className="heading">Product Add</div>
          <div style={{ padding: '15px' }}>
            <button>Save</button>
            <button onClick={() => navigate('/')}>Cancel</button>
          </div>
        </header>
        <hr />
        <section className="form">
          <form id="product_form" onSubmit={onSubmit}>
            <div className="form-group">
              <label>SKU</label>
              <input
                type="text"
                className="form-control"
                id="sku"
                name="SKU"
                placeholder="Enter SKU"
                onChange={onChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Name</label>
              <input
                type="text"
                className="form-control"
                id="name"
                name="name"
                placeholder="Enter product name"
                onChange={onChange}
              />
            </div>
            <div className="form-group">
              <label>Price ($)</label>
              <input
                type="number"
                className="form-control"
                id="price"
                name="price"
                placeholder="Enter product price"
                onChange={onChange}
              />
            </div>
            <div className="form-group">
              <label for="productType">Type Switcher</label>
              <select id="productType" name="productType">
                <option id="DVD" value="DVD">
                  DVD
                </option>
                <option id="Furniture" value="Furniture">
                  Furniture
                </option>
                <option id="Book" value="Book">
                  Book
                </option>
              </select>
            </div>
            {false && (
              <>
                <div className="form-group">
                  <label>Size (MB)</label>
                  <input
                    type="number"
                    className="form-control"
                    id="size"
                    name="size"
                    placeholder="Enter DVD SiZe"
                    onChange={onChange}
                  />
                </div>
                <p style={{ textAlign: 'left' }}>
                  Please Provide the size of the DVD{' '}
                </p>
              </>
            )}
            {true && (
              <>
                <div className="form-group">
                  <label>Height (CM)</label>
                  <input
                    type="number"
                    className="form-control"
                    id="height"
                    name="height"
                    placeholder="Enter Furniture height"
                    onChange={onChange}
                  />
                </div>
                <div className="form-group">
                  <label>Width (CM)</label>
                  <input
                    type="number"
                    className="form-control"
                    id="width"
                    name="width"
                    placeholder="Enter Furniture width"
                    onChange={onChange}
                  />
                </div>
                <div className="form-group">
                  <label>Length (CM)</label>
                  <input
                    type="number"
                    step={0.01}
                    className="form-control"
                    id="length"
                    name="length"
                    placeholder="Enter Furniture Length"
                    onChange={onChange}
                  />
                </div>
                <p style={{ textAlign: 'left' }}>
                  Please Provide dimensions in HxWxL format{' '}
                </p>
              </>
            )}
            {false && (
              <>
                <div className="form-group">
                  <label>Weight (KG)</label>
                  <input
                    type="number"
                    className="form-control"
                    id="weight"
                    name="weight"
                    placeholder="Enter Book Weight"
                  />
                </div>
                <p style={{ textAlign: 'left' }}>
                  Please Provide the weight of the Book{' '}
                </p>
              </>
            )}
          </form>
        </section>
      </div>
      <Footer />
    </>
  );
};

export default AddProductPage;
