import axios from 'axios';

const API_URL = 'http://localhost:8080/product';

// Get all Products
const getAllProduct = async () => {
  const response = await axios.get(API_URL);
  return response.data;
};

// Create a product


const productService = {
  getAllProduct
}

export default productService;
