import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import Spinner from '../components/Spinner';
import Footer from '../components/Footer';
import {
  reset,
  getAllProduct,
  deleteProduct,
} from '../features/product/productSlice';

const ProductListPage = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [productList, setProductList] = useState([]);
  const { products, isLoading, isError, message } = useSelector(
    (state) => state.product
  );
  const onChange = (e) => {
    setProductList((prev) => [...prev, parseInt(e.target.value)])
  }
  useEffect(() => {
    if (isError) {
      console.log(message);
    }
    dispatch(getAllProduct());
    return () => {
      dispatch(reset());
    };
  }, [navigate, isError, message, dispatch]);

  // if (isLoading) {
  //   return <Spinner />;
  // }
  return (
    <>
      <div className="container">
        <header>
          <div className="heading">Product List</div>
          <div style={{ padding: '15px' }}>
            <button onClick={() => navigate('/add-product')}>ADD</button>
            <button
              id="delete-product-btn"
              onClick={() => dispatch(deleteProduct(productList))}
            >
              MASS DELETE
            </button>
          </div>
        </header>
        <hr />
        {products.length > 0 ? (
          <div className="grid">
            {products.map((product) => (
              <div className="box">
                <input
                  className="delete-checkbox"
                  type="checkbox"
                  id={product.id}
                  name={product.name}
                  value={product.id}
                  onChange={(e)=> onChange(e)}
                />
                <br />
                <label htmlFor={product.id}>
                  <div className="box-text">
                    <p>{product.sku}</p>
                    <p>{product.name}</p>
                    <p>{product.price}$</p>
                    {product.type === 'VD' && product.weight && <p>Size: {product.weight} KG</p>}
                    {product.size && <p>Weight: {product.size} MB</p>}
                    {product.height && product.width && product.length && (
                      <p>
                        Dimension: {product.height} x {product.width} x{' '}
                        {product.length}
                      </p>
                    )}
                  </div>
                </label>
              </div>
            ))}
          </div>
        ) : (
          <div>No Product Availabe kindly Add Products</div>
        )}
      </div>
      <Footer />
    </>
  );
};

export default ProductListPage;
