import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import Spinner from '../components/Spinner';
import Footer from '../components/Footer';
import {
  reset,
  getAllProduct,
  deleteProduct,
} from '../features/product/productSlice';

const ProductListPage = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [productList, setProductList] = useState([]);
  const { products, isLoading, isError, message } = useSelector(
    (state) => state.product
  );
  const onChange = async (e) => {
    if (e.target.checked) {
      setProductList((prev) => [...prev, parseInt(e.target.value)]);
    } else {
      function myCallBack(el) {
        return productList.indexOf(el.id) < 0;
      }
      const newproducts = productList.filter(myCallBack)

    }

  };
  useEffect(() => {
    if (isError) {
      console.log(message);
    }
    dispatch(getAllProduct());
    return () => {
      dispatch(reset());
    };
  }, [navigate, isError, message, dispatch]);

  // if (isLoading) {
  //   return <Spinner />;
  // }
  return (
    <>
      <div className="container">
        <header>
          <div className="heading">Product List</div>
          <div style={{ padding: '15px' }}>
            <button onClick={() => navigate('/add-product')}>ADD</button>
            <button
              id="delete-product-btn"
              onClick={() => {
                dispatch(deleteProduct(productList));
                // reset();
                // function myCallBack(el) {
                //   return productList.indexOf(el.id) < 0;
                // }
                // products = products.filter(myCallBack)
                // dispatch(getAllProduct());
                setTimeout(() => {
                  dispatch(getAllProduct());
                }, 20);
              }}
            >
              MASS DELETE
            </button>
          </div>
        </header>
        <hr />
        {products.length > 0 ? (
          <div className="grid">
            {products.map((product, index) => (
              <div className="box" key={index}>
                <label htmlFor={product.id}>
                <input
                  className="delete-checkbox"
                  type="checkbox"
                  id={product.id}
                  name={product.name}
                  value={product.id}
                  onChange={(e) => onChange(e)}
                />
                <br />
                <div className="box-text">
                  <p>{product.sku}</p>
                  <p>{product.name}</p>
                  <p>{product.price}$</p>
                  {product.type === 'Book' && product.weight && (
                    <p>Size: {product.weight} KG</p>
                  )}
                  {product.type === 'DVD' && product.size && (
                    <p>Weight: {product.size} MB</p>
                  )}
                  {product.type === 'Furniture' &&
                    product.height &&
                    product.width &&
                    product.length && (
                      <p>
                        Dimension: {product.height} x {product.width} x{' '}
                        {product.length}
                      </p>
                    )}
                </div>
                </label>

              </div>
            ))}
          </div>
        ) : (
          <div>No Product Availabe kindly Add Products</div>
        )}
      </div>
      <Footer />
    </>
  );
};

export default ProductListPage;
