import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { reset, createProduct } from '../features/product/productSlice';
import Footer from '../components/Footer';
const AddProductPage = () => {
  const [productData, setProductData] = useState({
    name: '',
    type: '',
    SKU: '',
    price: null,
    size: null,
    weight: null,
    height: null,
    width: null,
    length: null,
  });
  const [err, serErr] = useState('hi');
  const check = (e) => {
    // e.target.value
    serErr(`${e.target.name} is required`);
  };
  const { name, type, SKU, price, size, weight, height, width, length } =
    productData;
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const onChange = (e) => {
    console.log(e);
    setProductData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const onSubmit = (e) => {
    e.preventDefault();
    console.log(productData);
    if (!name) {
      // toast.error('Passwords do not match');
      return;
    } else {
      const data = {
        name,
        type,
        SKU,
        price,
        size,
        weight,
        height,
        width,
        length,
      };
      // dispatch(createProduct(data));
    }
  };
  // const onSubmit = (e) => {
  //   e.preventDefault();
  //   dispatch(createProduct(productData));
  //   // productData([]);
  // };

  return (
    <>
      <div className="container">
        <form id="product_form" onSubmit={onSubmit}>
          <header>
            <div className="heading">Product Add</div>
            <div style={{ padding: '15px' }}>
              <button type="submit">Save</button>
              <button onClick={() => navigate('/')}>Cancel</button>
            </div>
          </header>
          <hr />
          <section className="form">
            <div className="form-group">
              <label>SKU</label>
              <input
                type="text"
                className="form-control"
                id="sku"
                name="SKU"
                placeholder="Enter SKU"
                onChange={onChange}
                value={SKU}
                required
                onInvalid={(F) => F.target.setCustomValidity(err)}
                onInput={(F) => check(F)}
              />
            </div>
            <div className="form-group">
              <label>Name</label>
              <input
                type="text"
                className="form-control"
                id="name"
                name="name"
                placeholder="Enter product name"
                onChange={onChange}
                value={name}
                required
                onInvalid={(F) => F.target.setCustomValidity(err)}
                onInput={(F) => check(F)}
              />
            </div>
            <div className="form-group">
              <label>Price ($)</label>
              <input
                type="number"
                className="form-control"
                id="price"
                name="price"
                placeholder="Enter product price"
                value={price}
                onChange={onChange}
                required
                onInvalid={(F) => F.target.setCustomValidity(err)}
                onInput={(F) => check(F)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="type">Type Switcher</label>
              <select id="productType" name="type" onChange={onChange} required>
                <option value="">None</option>
                <option id="DVD" value="DVD">
                  DVD
                </option>
                <option id="Furniture" value="Furniture">
                  Furniture
                </option>
                <option id="Book" value="Book">
                  Book
                </option>
              </select>
            </div>
            {type === 'DVD' && (
              <>
                <div className="form-group">
                  <label>Size (MB)</label>
                  <input
                    type="number"
                    className="form-control"
                    id="size"
                    name="size"
                    placeholder="Enter DVD Size"
                    value={size}
                    required={true}
                    onInvalid={(F) =>
                      F.target.setCustomValidity('Please Submit Size')
                    }
                    onInput={(F) => F.target.setCustomValidity('')}
                    onChange={onChange}
                  />
                </div>
                <p style={{ textAlign: 'left' }}>
                  Please Provide the size of the DVD{' '}
                </p>
              </>
            )}
            {type === 'Furniture' && (
              <>
                <div className="form-group">
                  <label>Height (CM)</label>
                  <input
                    type="number"
                    className="form-control"
                    id="height"
                    name="height"
                    placeholder="Enter Furniture height"
                    value={height}
                    onChange={onChange}
                    required
                    onInvalid={(F) =>
                      F.target.setCustomValidity('Please Submit Height')
                    }
                    onInput={(F) => F.target.setCustomValidity('')}
                  />
                </div>
                <div className="form-group">
                  <label>Width (CM)</label>
                  <input
                    type="number"
                    className="form-control"
                    id="width"
                    name="width"
                    placeholder="Enter Furniture width"
                    value={width}
                    onChange={onChange}
                    required
                    onInvalid={(F) =>
                      F.target.setCustomValidity('Please Submit Name')
                    }
                    onInput={(F) => F.target.setCustomValidity('')}
                  />
                </div>
                <div className="form-group">
                  <label>Length (CM)</label>
                  <input
                    type="number"
                    step={0.01}
                    className="form-control"
                    id="length"
                    name="length"
                    value={length}
                    placeholder="Enter Furniture Length"
                    onChange={onChange}
                    required
                    onInvalid={(F) =>
                      F.target.setCustomValidity('Please Submit Name')
                    }
                    onInput={(F) => F.target.setCustomValidity('')}
                  />
                </div>
                <p style={{ textAlign: 'left' }}>
                  Please Provide dimensions in HxWxL format{' '}
                </p>
              </>
            )}
            {type === 'Book' && (
              <>
                <div className="form-group">
                  <label>Weight (KG)</label>
                  <input
                    type="number"
                    className="form-control"
                    id="weight"
                    name="weight"
                    value={weight}
                    placeholder="Enter Book Weight"
                    onChange={onChange}
                  />
                </div>
                <p style={{ textAlign: 'left' }}>
                  Please Provide the weight of the Book{' '}
                </p>
              </>
            )}
          </section>
        </form>
      </div>
      <Footer />
    </>
  );
};

export default AddProductPage;
